package language.implementation.symbols.declarations;

import language.implementation.Visitor;
import language.implementation.symbols.Declaration;
import language.implementation.symbols.terminals.Identifier;

public class VarDecl extends Declaration{
	private static final long serialVersionUID = 1L;

	public final Identifier id;
	
	@SuppressWarnings("unchecked")
	public VarDecl(Identifier id) {
		super(VarDecl.class, Identifier.class);
		this.id = id;
	}

	@Override
	public void accept(Visitor visitor) {
		visitor.visit(this);
	}

	//generated by eclipse
	@Override
	public int hashCode() {
		final int prime = 31;
		int result = 1;
		result = prime * result + ((id == null) ? 0 : id.hashCode());
		return result;
	}

	//generated by eclipse
	@Override
	public boolean equals(Object obj) {
		if (this == obj)
			return true;
		if (obj == null)
			return false;
		if (getClass() != obj.getClass())
			return false;
		VarDecl other = (VarDecl) obj;
		if (id == null) {
			if (other.id != null)
				return false;
		} else if (!id.equals(other.id))
			return false;
		return true;
	}
	
	
	
}
